const l=a=>{if(!a)return"";try{const t=new Date(a),r=String(t.getDate()).padStart(2,"0"),e=String(t.getMonth()+1).padStart(2,"0"),n=t.getFullYear();return`${r}-${e}-${n}`}catch(t){return console.error("Error formatting date:",t),""}},u=a=>{const t=new Date,r=new Date(a),e=t-r,n=Math.floor(e/6e4),o=Math.floor(e/36e5),s=Math.floor(e/864e5);if(n<1)return"baru saja";if(n<60)return`${n} menit yang lalu`;if(o<24)return`${o} jam yang lalu`;if(s===1)return"kemarin";{const f=String(r.getDate()).padStart(2,"0"),c=String(r.getMonth()+1).padStart(2,"0"),i=r.getFullYear();return`${f}/${c}/${i}`}};export{l as a,u as f};
