const d=e=>{if(!e)return"";try{const t=new Date(e),n=String(t.getDate()).padStart(2,"0"),a=String(t.getMonth()+1).padStart(2,"0"),r=t.getFullYear();return`${n}-${a}-${r}`}catch(t){return console.error("Error formatting date:",t),""}},S=e=>{const t=new Date,n=new Date(e),a=t-n,r=Math.floor(a/6e4),o=Math.floor(a/36e5),s=Math.floor(a/864e5);if(r<1)return"baru saja";if(r<60)return`${r} menit yang lalu`;if(o<24)return`${o} jam yang lalu`;if(s===1)return"kemarin";{const i=String(n.getDate()).padStart(2,"0"),c=String(n.getMonth()+1).padStart(2,"0"),g=n.getFullYear();return`${i}/${c}/${g}`}},m=e=>{const t=new Date(e),n=t.getHours(),a=t.getMinutes(),r=t.getDate(),o=t.getMonth()+1,s=t.getFullYear(),i=String(n).padStart(2,"0"),c=String(a).padStart(2,"0"),g=String(r).padStart(2,"0"),u=String(o).padStart(2,"0"),f=`${i}.${c} WIB`,l=`${g}-${u}-${s}`;return`${f}, ${l}`};export{d as a,m as b,S as f};
